$(document).ready(function(t){t("#getQuote").on("click",function(){t.ajaxSetup({cache:!1}),t.getJSON("http://mysafeinfo.com/api/data?list=moviequotes&format=json",function(e){t("#quote").slideDown();var o=Math.floor(Math.random()*e.length);t(".quoteHere").append('<img src="img/quote-left.png" alt="img"> <h3>'+e[o].qt+"</h3><p>from: "+e[o].ref+"</p>")}),t("#quote").empty()})});